import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Typed from "typed.js";
import { useEffect, useRef } from 'react';
import _default from 'react-bootstrap/esm/Offcanvas';

 const  Home = () => {

    const topIndex  = {
        zIndex: '999',
        width: '60%'
    }

    const topline = {
        fontSize: '20px',
        letterSpacing: '5px',
        color: '#1f2235'
    }
    const desHeading = {
        display:'block',
        fontSize: '3em',
        fontWeight: '800',
        color: '#1f2235'
    }

    const multiText = {
        color : '#7f00ff'
    }

    const el = useRef(null);

    useEffect(() => {
        const typed = new Typed(el.current, {
          strings: ["Coder", "Freelancer"], 
          startDelay: 300,
          typeSpeed: 200,
          backSpeed: 300,
          backDelay: 100,
          smartBackspace: true,
          loop: true,
          showCursor: true
        });
    
        // Destropying
        return () => {
          typed.destroy();
        };
      }, []);
    
  

    return(
        <Container fluid="sm">
            <Row>
                <Col xs={12} md={8} lg={8}>
                <div class="col-lg-7 " style={topIndex}>
                        <span style={topline}>Hello<span class="helloUnderscore"></span></span>
                        <h1 style={desHeading}>I'm <span style={multiText} ref={el}></span></h1>
                        <p class="descriptionText">I'm Yasin, professional web developer with long time experience in the field</p>
                        <div>
                            <button class="btn btns">Download CV</button>
                            <button class="btn btns"  type="button" data-toggle="modal" data-target="#myModal">Connect me</button>
                        </div>
                    </div>
                </Col>
                <Col xs={12} md={4} lg={4} style={{backgroundColor : 'green'}}>
                xs=6 md=4
                </Col>
            </Row>
        </Container>
    ) 
}

export default Home;
